mapper.select:

select

  distinct


from compra_proveedor




  order by ${orderByClause}


	LIMIT ${limit}

  	SELECT id_compra_proveedor AS id_compra, compra.id_proveedor, prov.nombre As nombreProv, compra.id_establecimiento, estab.nombre AS nombreEstab,
  	valor, fecha_compra,
  	IF( motivo IS NULL OR motivo = '', 'Alta', 'Baja') as estado, motivo,
  	IF( pagado = true, 'SI', 'NO') as pagado
  	FROM compra_proveedor compra
  	LEFT JOIN proveedor prov ON prov.id_proveedor = compra.id_proveedor 
  	LEFT JOIN establecimiento estab ON estab.id_establecimiento = compra.id_establecimiento
	WHERE 1 = 1
		
			AND id_compra_proveedor = #{compra}
		
		
			AND pagado = TRUE
		
		
			AND (pagado = FALSE OR pagado IS NULL)
		
		
			AND motivo != ''
		
		
			AND (motivo = '' OR pagado IS NULL)
		
		
			AND compra.id_proveedor = #{prov}
		
		
			AND compra.id_establecimiento = #{estab}
		
		
			AND valor >= #{valorMayor}
		
		
			AND valor <= #{valorMenor}
		
		
			AND fecha_compra Between #{fechaI} AND #{fechaF} 
		
		ORDER BY id_compra_proveedor DESC
		LIMIT ${limit};

  	SELECT COUNT(*)
  	FROM compra_proveedor compra
  	LEFT JOIN proveedor prov ON prov.id_proveedor = compra.id_proveedor 
  	LEFT JOIN establecimiento estab ON estab.id_establecimiento = compra.id_establecimiento
	WHERE 1 = 1
		
			AND id_compra_proveedor = #{compra}
		
		
			AND pagado = TRUE
		
		
			AND (pagado = FALSE OR pagado IS NULL)
		
		
			AND motivo != ''
		
		
			AND (motivo = '' OR pagado IS NULL)
		
		
			AND compra.id_proveedor = #{prov}
		
		
			AND compra.id_establecimiento = #{estab}
		
		
			AND valor >= #{valorMayor}
		
		
			AND valor <= #{valorMenor}
		
		
			AND fecha_compra Between #{fechaI} AND #{fechaF}
